mapper.update:
update tbl_member
set member_name = #{memberName}, member_email = #{memberEmail}, member_password = #{memberPassword}, member_phone = #{memberPhone}
where member_number = #{memberNumber}

mapper.select:

select ms.message_number, ms.message_article, ms.message_send_date, member_name 
from tbl_message ms join tbl_member m on message_send_email = member_number and message_recive_email = #{messageReciveEmail}
order by message_number desc limit #{startRow}, #{rowCount}
